using UnityEngine;
using UnityEngine.UI;

public class SettingsManager : MonoBehaviour
{
    [Header("Settings Popup")]
    [SerializeField] private GameObject settingsPopup;

    [Header("Setting Buttons")]
    [SerializeField] private Button soundButton;
    [SerializeField] private Button musicButton;
    [SerializeField] private Button hapticsButton;
    [SerializeField] private Button closeButton;

    [Header("Button Images")]
    [SerializeField] private Image soundButtonImage;
    [SerializeField] private Image musicButtonImage;
    [SerializeField] private Image hapticsButtonImage;

    [Header("On/Off Sprites")]
    [SerializeField] private Sprite soundOnSprite;
    [SerializeField] private Sprite soundOffSprite;
    [SerializeField] private Sprite musicOnSprite;
    [SerializeField] private Sprite musicOffSprite;
    [SerializeField] private Sprite hapticsOnSprite;
    [SerializeField] private Sprite hapticsOffSprite;

    // PlayerPrefs keys
    private const string SOUND_KEY = "Settings_Sound";
    private const string MUSIC_KEY = "Settings_Music";
    private const string HAPTICS_KEY = "Settings_Haptics";

    // Current states
    private bool soundEnabled = true;
    private bool musicEnabled = true;
    private bool hapticsEnabled = true;

    private void Start()
    {
        // Load saved settings
        LoadSettings();

        // Update button visuals
        UpdateButtonVisuals();

        // Hide popup initially
        if (settingsPopup != null)
        {
            settingsPopup.SetActive(false);
        }

        // Setup button listeners
        if (soundButton != null)
            soundButton.onClick.AddListener(ToggleSound);

        if (musicButton != null)
            musicButton.onClick.AddListener(ToggleMusic);

        if (hapticsButton != null)
            hapticsButton.onClick.AddListener(ToggleHaptics);

        if (closeButton != null)
            closeButton.onClick.AddListener(CloseSettings);
    }

    private void LoadSettings()
    {
        // Load settings from PlayerPrefs (default to 1 = enabled)
        soundEnabled = PlayerPrefs.GetInt(SOUND_KEY, 1) == 1;
        musicEnabled = PlayerPrefs.GetInt(MUSIC_KEY, 1) == 1;
        hapticsEnabled = PlayerPrefs.GetInt(HAPTICS_KEY, 1) == 1;
    }

    private void SaveSettings()
    {
        PlayerPrefs.SetInt(SOUND_KEY, soundEnabled ? 1 : 0);
        PlayerPrefs.SetInt(MUSIC_KEY, musicEnabled ? 1 : 0);
        PlayerPrefs.SetInt(HAPTICS_KEY, hapticsEnabled ? 1 : 0);
        PlayerPrefs.Save();
    }

    public void ShowSettings()
    {
        if (settingsPopup != null)
        {
            settingsPopup.SetActive(true);
        }
    }

    public void CloseSettings()
    {
        if (settingsPopup != null)
        {
            settingsPopup.SetActive(false);
        }
    }

    private void ToggleSound()
    {
        soundEnabled = !soundEnabled;
        SaveSettings();
        UpdateButtonVisuals();

        // Here you can add actual sound control logic
        // For example: AudioListener.volume = soundEnabled ? 1f : 0f;
    }

    private void ToggleMusic()
    {
        musicEnabled = !musicEnabled;
        SaveSettings();
        UpdateButtonVisuals();

        // Here you can add actual music control logic
        // For example: if you have a music manager, call it here
    }

    private void ToggleHaptics()
    {
        hapticsEnabled = !hapticsEnabled;
        SaveSettings();
        UpdateButtonVisuals();

        // Here you can add actual haptics control logic
        // For example: Handheld.Vibrate() control
    }

    private void UpdateButtonVisuals()
    {
        // Update sound button
        if (soundButtonImage != null && soundOnSprite != null && soundOffSprite != null)
        {
            soundButtonImage.sprite = soundEnabled ? soundOnSprite : soundOffSprite;
        }

        // Update music button
        if (musicButtonImage != null && musicOnSprite != null && musicOffSprite != null)
        {
            musicButtonImage.sprite = musicEnabled ? musicOnSprite : musicOffSprite;
        }

        // Update haptics button
        if (hapticsButtonImage != null && hapticsOnSprite != null && hapticsOffSprite != null)
        {
            hapticsButtonImage.sprite = hapticsEnabled ? hapticsOnSprite : hapticsOffSprite;
        }
    }

    // Public getters for other scripts to check settings
    public bool IsSoundEnabled() => soundEnabled;
    public bool IsMusicEnabled() => musicEnabled;
    public bool IsHapticsEnabled() => hapticsEnabled;
}

using UnityEngine;
using UnityEditor;

public class ImportSpritesAsUI
{
    [MenuItem("Tools/Import UI Sprites")]
    public static void ImportUISprites()
    {
        string[] spritePaths = new string[]
        {
            "Assets/Sprites/UI/SettingsIcon.png",
            "Assets/Sprites/UI/SoundOn.png",
            "Assets/Sprites/UI/SoundOff.png",
            "Assets/Sprites/UI/MusicOn.png",
            "Assets/Sprites/UI/MusicOff.png",
            "Assets/Sprites/UI/HapticsOn.png",
            "Assets/Sprites/UI/HapticsOff.png",
            "Assets/Sprites/UI/CloseIcon.png"
        };

        foreach (string path in spritePaths)
        {
            TextureImporter importer = AssetImporter.GetAtPath(path) as TextureImporter;
            if (importer != null)
            {
                importer.textureType = TextureImporterType.Sprite;
                importer.spriteImportMode = SpriteImportMode.Single;
                importer.mipmapEnabled = false;
                importer.filterMode = FilterMode.Bilinear;
                importer.textureCompression = TextureImporterCompression.Uncompressed;

                AssetDatabase.ImportAsset(path, ImportAssetOptions.ForceUpdate);
                Debug.Log($"Imported sprite: {path}");
            }
            else
            {
                Debug.LogWarning($"Could not find texture at: {path}");
            }
        }

        AssetDatabase.Refresh();
        Debug.Log("All UI sprites imported successfully!");
    }
}

using UnityEngine;

public class HideSettingsPopup
{
    public static void Execute()
    {
        GameObject settingsPopup = GameObject.Find("Canvas/SettingsPopup");
        if (settingsPopup != null)
        {
            settingsPopup.SetActive(false);
            Debug.Log("Settings popup hidden successfully!");
        }
        else
        {
            Debug.LogWarning("Settings popup not found!");
        }
    }
}

using UnityEngine;
using System.Collections.Generic;

public class AudioManager : MonoBehaviour
{
    public static AudioManager Instance { get; private set; }

    [Header("Audio Sources")]
    [SerializeField] private AudioSource musicSource;
    [SerializeField] private AudioSource sfxSource;

    [Header("Audio Clips")]
    [SerializeField] private AudioClip[] musicClips;
    [SerializeField] private AudioClip[] sfxClips;

    [Header("Settings")]
    [SerializeField] private float musicVolume = 1f;
    [SerializeField] private float sfxVolume = 1f;

    // Pool of audio sources for overlapping sound effects
    private List<AudioSource> sfxSourcePool = new List<AudioSource>();
    private const int INITIAL_SFX_POOL_SIZE = 5;

    // Current state
    private bool musicEnabled = true;
    private bool sfxEnabled = true;
    private int currentMusicIndex = -1;

    private void Awake()
    {
        // Singleton pattern
        if (Instance != null && Instance != this)
        {
            Destroy(gameObject);
            return;
        }

        Instance = this;
        DontDestroyOnLoad(gameObject);

        InitializeAudioSources();
    }

    private void InitializeAudioSources()
    {
        // Create music source if not assigned
        if (musicSource == null)
        {
            musicSource = gameObject.AddComponent<AudioSource>();
            musicSource.loop = true;
            musicSource.playOnAwake = false;
        }

        // Create main SFX source if not assigned
        if (sfxSource == null)
        {
            sfxSource = gameObject.AddComponent<AudioSource>();
            sfxSource.loop = false;
            sfxSource.playOnAwake = false;
        }

        // Create SFX source pool for overlapping sounds
        for (int i = 0; i < INITIAL_SFX_POOL_SIZE; i++)
        {
            AudioSource source = gameObject.AddComponent<AudioSource>();
            source.loop = false;
            source.playOnAwake = false;
            sfxSourcePool.Add(source);
        }

        // Apply initial volumes
        UpdateMusicVolume();
        UpdateSFXVolume();
    }

    #region Music Control

    /// <summary>
    /// Play background music by clip
    /// </summary>
    public void PlayMusic(AudioClip clip, bool loop = true)
    {
        if (clip == null)
        {
            Debug.LogWarning("AudioManager: Attempted to play null music clip");
            return;
        }

        musicSource.clip = clip;
        musicSource.loop = loop;
        musicSource.Play();
    }

    /// <summary>
    /// Play background music by index from musicClips array
    /// </summary>
    public void PlayMusic(int index, bool loop = true)
    {
        if (musicClips == null || index < 0 || index >= musicClips.Length)
        {
            Debug.LogWarning($"AudioManager: Invalid music index {index}");
            return;
        }

        currentMusicIndex = index;
        PlayMusic(musicClips[index], loop);
    }

    /// <summary>
    /// Play background music by name
    /// </summary>
    public void PlayMusicByName(string clipName, bool loop = true)
    {
        if (musicClips == null) return;

        for (int i = 0; i < musicClips.Length; i++)
        {
            if (musicClips[i] != null && musicClips[i].name == clipName)
            {
                PlayMusic(i, loop);
                return;
            }
        }

        Debug.LogWarning($"AudioManager: Music clip '{clipName}' not found");
    }

    /// <summary>
    /// Stop currently playing music
    /// </summary>
    public void StopMusic()
    {
        musicSource.Stop();
        currentMusicIndex = -1;
    }

    /// <summary>
    /// Pause currently playing music
    /// </summary>
    public void PauseMusic()
    {
        musicSource.Pause();
    }

    /// <summary>
    /// Resume paused music
    /// </summary>
    public void ResumeMusic()
    {
        musicSource.UnPause();
    }

    /// <summary>
    /// Check if music is currently playing
    /// </summary>
    public bool IsMusicPlaying()
    {
        return musicSource.isPlaying;
    }

    #endregion

    #region Sound Effects Control

    /// <summary>
    /// Play a one-shot sound effect
    /// </summary>
    public void PlaySFX(AudioClip clip, float volumeScale = 1f)
    {
        if (clip == null)
        {
            Debug.LogWarning("AudioManager: Attempted to play null SFX clip");
            return;
        }

        if (!sfxEnabled) return;

        // Try to find an available audio source from the pool
        AudioSource availableSource = GetAvailableSFXSource();

        if (availableSource != null)
        {
            availableSource.volume = sfxVolume * volumeScale;
            availableSource.PlayOneShot(clip);
        }
        else
        {
            // Fallback to main SFX source
            sfxSource.PlayOneShot(clip, volumeScale);
        }
    }

    /// <summary>
    /// Play a sound effect by index from sfxClips array
    /// </summary>
    public void PlaySFX(int index, float volumeScale = 1f)
    {
        if (sfxClips == null || index < 0 || index >= sfxClips.Length)
        {
            Debug.LogWarning($"AudioManager: Invalid SFX index {index}");
            return;
        }

        PlaySFX(sfxClips[index], volumeScale);
    }

    /// <summary>
    /// Play a sound effect by name
    /// </summary>
    public void PlaySFXByName(string clipName, float volumeScale = 1f)
    {
        if (sfxClips == null) return;

        for (int i = 0; i < sfxClips.Length; i++)
        {
            if (sfxClips[i] != null && sfxClips[i].name == clipName)
            {
                PlaySFX(i, volumeScale);
                return;
            }
        }

        Debug.LogWarning($"AudioManager: SFX clip '{clipName}' not found");
    }

    /// <summary>
    /// Get an available audio source from the pool
    /// </summary>
    private AudioSource GetAvailableSFXSource()
    {
        foreach (AudioSource source in sfxSourcePool)
        {
            if (!source.isPlaying)
            {
                return source;
            }
        }

        // If all sources are busy, create a new one
        AudioSource newSource = gameObject.AddComponent<AudioSource>();
        newSource.loop = false;
        newSource.playOnAwake = false;
        sfxSourcePool.Add(newSource);

        return newSource;
    }

    #endregion

    #region Volume Control

    /// <summary>
    /// Set music enabled/disabled
    /// </summary>
    public void SetMusicEnabled(bool enabled)
    {
        musicEnabled = enabled;
        UpdateMusicVolume();
    }

    /// <summary>
    /// Set sound effects enabled/disabled
    /// </summary>
    public void SetSFXEnabled(bool enabled)
    {
        sfxEnabled = enabled;
        UpdateSFXVolume();
    }

    /// <summary>
    /// Set music volume (0-1)
    /// </summary>
    public void SetMusicVolume(float volume)
    {
        musicVolume = Mathf.Clamp01(volume);
        UpdateMusicVolume();
    }

    /// <summary>
    /// Set SFX volume (0-1)
    /// </summary>
    public void SetSFXVolume(float volume)
    {
        sfxVolume = Mathf.Clamp01(volume);
        UpdateSFXVolume();
    }

    /// <summary>
    /// Update music source volume based on enabled state
    /// </summary>
    private void UpdateMusicVolume()
    {
        musicSource.volume = musicEnabled ? musicVolume : 0f;
    }

    /// <summary>
    /// Update all SFX sources volume based on enabled state
    /// </summary>
    private void UpdateSFXVolume()
    {
        float volume = sfxEnabled ? sfxVolume : 0f;

        sfxSource.volume = volume;

        foreach (AudioSource source in sfxSourcePool)
        {
            source.volume = volume;
        }
    }

    #endregion

    #region Getters

    public bool IsMusicEnabled() => musicEnabled;
    public bool IsSFXEnabled() => sfxEnabled;
    public float GetMusicVolume() => musicVolume;
    public float GetSFXVolume() => sfxVolume;

    #endregion
}

