using UnityEngine;
using System.Collections.Generic;
using System.Linq;

namespace BrickDigger
{
    // Cell coordinate in the grid
    [System.Serializable]
    public struct CellCoord
    {
        public int x;
        public int y;

        public CellCoord(int x, int y)
        {
            this.x = x;
            this.y = y;
        }

        public static CellCoord operator +(CellCoord a, CellCoord b)
        {
            return new CellCoord(a.x + b.x, a.y + b.y);
        }

        public Vector3 ToWorldPosition(float height = 0)
        {
            return new Vector3(x, height, y);
        }

        public override string ToString() => $"({x}, {y})";
    }

    // Different block types
    public enum BlockType
    {
        Air,
        Dirt,
        Bedrock,
        CoinBlock,
        LegoPiece
    }

    // Grid cell data
    [System.Serializable]
    public class GridCell
    {
        public BlockType topLayer;    // Layer 1 (dirt, coin blocks, or air)
        public BlockType bottomLayer; // Layer 0 (bedrock or lego piece)
        public int pieceId = -1;      // ID of the lego piece if this is a piece cell

        public GridCell()
        {
            topLayer = BlockType.Dirt;
            bottomLayer = BlockType.Bedrock;
        }
    }

    // Main grid manager
    public class GridManager : MonoBehaviour
    {
        [Header("Grid Settings")]
        [SerializeField] private int width = 7;
        [SerializeField] private int height = 15;

        [Header("Block Prefabs")]
        [SerializeField] private GameObject dirtBlockPrefab;
        [SerializeField] private GameObject bedrockBlockPrefab;
        [SerializeField] private GameObject coinBlockPrefab;
        [SerializeField] private GameObject legoPiecePrefab;

        [Header("Visual Settings")]
        [SerializeField] private float blockSize = 1f;
        [SerializeField] private float layerHeight = 1f;

        private GridCell[,] grid;
        private Dictionary<CellCoord, GameObject> topLayerBlocks = new Dictionary<CellCoord, GameObject>();
        private Dictionary<CellCoord, GameObject> bottomLayerBlocks = new Dictionary<CellCoord, GameObject>();

        // Highlighted block for digging
        private GameObject highlightedBlock;
        private CellCoord highlightedCoord;

        public int Width => width;
        public int Height => height;

        private void Awake()
        {
            InitializeGrid();
        }

        private void InitializeGrid()
        {
            grid = new GridCell[width, height];
            for (int x = 0; x < width; x++)
            {
                for (int y = 0; y < height; y++)
                {
                    grid[x, y] = new GridCell();
                }
            }
        }

        public void GenerateLevel(LevelConfig config)
        {
            width = config.width;
            height = config.height;
            InitializeGrid();

            // Clear existing blocks
            ClearAllBlocks();

            // Generate bedrock layer
            GenerateBedrockLayer();

            // Place lego piece
            PlaceLegoPiece(config.pieceShape, config.piecePosition);

            // Generate dirt layer with coins
            GenerateDirtLayer(config.coinsCount);

            // Spawn blocks in world
            SpawnAllBlocks();
        }

        private void GenerateBedrockLayer()
        {
            for (int x = 0; x < width; x++)
            {
                for (int y = 0; y < height; y++)
                {
                    grid[x, y].bottomLayer = BlockType.Bedrock;
                }
            }
        }

        private void PlaceLegoPiece(List<CellCoord> shape, CellCoord position)
        {
            foreach (var coord in shape)
            {
                CellCoord worldCoord = position + coord;
                if (IsValidCoord(worldCoord))
                {
                    grid[worldCoord.x, worldCoord.y].bottomLayer = BlockType.LegoPiece;
                    grid[worldCoord.x, worldCoord.y].pieceId = 0;
                }
            }
        }

        private void GenerateDirtLayer(int coinsCount)
        {
            // Fill with dirt
            for (int x = 0; x < width; x++)
            {
                for (int y = 0; y < height; y++)
                {
                    grid[x, y].topLayer = BlockType.Dirt;
                }
            }

            // Place random coins
            List<CellCoord> availableCoords = new List<CellCoord>();
            for (int x = 0; x < width; x++)
            {
                for (int y = 0; y < height; y++)
                {
                    availableCoords.Add(new CellCoord(x, y));
                }
            }

            for (int i = 0; i < coinsCount && availableCoords.Count > 0; i++)
            {
                int randomIndex = Random.Range(0, availableCoords.Count);
                CellCoord coord = availableCoords[randomIndex];
                grid[coord.x, coord.y].topLayer = BlockType.CoinBlock;
                availableCoords.RemoveAt(randomIndex);
            }
        }

        private void SpawnAllBlocks()
        {
            // Spawn bottom layer
            for (int x = 0; x < width; x++)
            {
                for (int y = 0; y < height; y++)
                {
                    CellCoord coord = new CellCoord(x, y);
                    SpawnBottomBlock(coord, grid[x, y].bottomLayer);
                }
            }

            // Spawn top layer
            for (int x = 0; x < width; x++)
            {
                for (int y = 0; y < height; y++)
                {
                    CellCoord coord = new CellCoord(x, y);
                    if (grid[x, y].topLayer != BlockType.Air)
                    {
                        SpawnTopBlock(coord, grid[x, y].topLayer);
                    }
                }
            }
        }

        private void SpawnTopBlock(CellCoord coord, BlockType type)
        {
            GameObject prefab = GetBlockPrefab(type);
            if (prefab != null)
            {
                Vector3 position = coord.ToWorldPosition(layerHeight);
                GameObject block = Instantiate(prefab, position, Quaternion.identity, transform);
                topLayerBlocks[coord] = block;
            }
        }

        private void SpawnBottomBlock(CellCoord coord, BlockType type)
        {
            GameObject prefab = GetBlockPrefab(type);
            if (prefab != null)
            {
                Vector3 position = coord.ToWorldPosition(0);
                GameObject block = Instantiate(prefab, position, Quaternion.identity, transform);
                bottomLayerBlocks[coord] = block;
            }
        }

        private GameObject GetBlockPrefab(BlockType type)
        {
            switch (type)
            {
                case BlockType.Dirt:
                    return dirtBlockPrefab;
                case BlockType.Bedrock:
                    return bedrockBlockPrefab;
                case BlockType.CoinBlock:
                    return coinBlockPrefab;
                case BlockType.LegoPiece:
                    return legoPiecePrefab;
                default:
                    return null;
            }
        }

        public bool DigBlock(CellCoord coord, out bool foundCoin, out bool foundPiece)
        {
            foundCoin = false;
            foundPiece = false;

            if (!IsValidCoord(coord))
                return false;

            GridCell cell = grid[coord.x, coord.y];

            if (cell.topLayer == BlockType.Air)
                return false;

            // Check what we're digging
            if (cell.topLayer == BlockType.CoinBlock)
            {
                foundCoin = true;
            }

            // Check if piece is underneath
            if (cell.bottomLayer == BlockType.LegoPiece)
            {
                foundPiece = true;
            }

            // Remove the block
            cell.topLayer = BlockType.Air;

            // Destroy the visual block
            if (topLayerBlocks.ContainsKey(coord))
            {
                // Play break animation here
                StartCoroutine(BreakBlockAnimation(topLayerBlocks[coord]));
                topLayerBlocks.Remove(coord);
            }

            return true;
        }

        private System.Collections.IEnumerator BreakBlockAnimation(GameObject block)
        {
            // Simple break animation - scale down and destroy
            float duration = 0.3f;
            float elapsed = 0;
            Vector3 originalScale = block.transform.localScale;

            while (elapsed < duration)
            {
                elapsed += Time.deltaTime;
                float t = elapsed / duration;
                block.transform.localScale = Vector3.Lerp(originalScale, Vector3.zero, t);
                yield return null;
            }

            Destroy(block);
        }

        public void HighlightBlock(CellCoord coord)
        {
            if (highlightedCoord.x == coord.x && highlightedCoord.y == coord.y)
                return;

            ClearHighlight();

            if (topLayerBlocks.ContainsKey(coord))
            {
                highlightedBlock = topLayerBlocks[coord];
                highlightedCoord = coord;

                // Add highlight effect
                Renderer renderer = highlightedBlock.GetComponent<Renderer>();
                if (renderer != null)
                {
                    // Store original color and make it brighter
                    renderer.material.EnableKeyword("_EMISSION");
                    renderer.material.SetColor("_EmissionColor", Color.yellow * 0.3f);
                }
            }
        }

        public void ClearHighlight()
        {
            if (highlightedBlock != null)
            {
                Renderer renderer = highlightedBlock.GetComponent<Renderer>();
                if (renderer != null)
                {
                    renderer.material.DisableKeyword("_EMISSION");
                }
                highlightedBlock = null;
            }
        }

        public BlockType GetTopBlockAt(CellCoord coord)
        {
            if (!IsValidCoord(coord))
                return BlockType.Air;
            return grid[coord.x, coord.y].topLayer;
        }

        public BlockType GetBottomBlockAt(CellCoord coord)
        {
            if (!IsValidCoord(coord))
                return BlockType.Bedrock;
            return grid[coord.x, coord.y].bottomLayer;
        }

        public bool IsValidCoord(CellCoord coord)
        {
            return coord.x >= 0 && coord.x < width && coord.y >= 0 && coord.y < height;
        }

        public CellCoord WorldToGrid(Vector3 worldPos)
        {
            return new CellCoord(
                Mathf.RoundToInt(worldPos.x),
                Mathf.RoundToInt(worldPos.z)
            );
        }

        public int CountLegoPieces()
        {
            int count = 0;
            for (int x = 0; x < width; x++)
            {
                for (int y = 0; y < height; y++)
                {
                    if (grid[x, y].bottomLayer == BlockType.LegoPiece)
                        count++;
                }
            }
            return count;
        }

        public int CountRevealedPieces()
        {
            int count = 0;
            for (int x = 0; x < width; x++)
            {
                for (int y = 0; y < height; y++)
                {
                    if (grid[x, y].bottomLayer == BlockType.LegoPiece && 
                        grid[x, y].topLayer == BlockType.Air)
                        count++;
                }
            }
            return count;
        }

        private void ClearAllBlocks()
        {
            foreach (var block in topLayerBlocks.Values)
            {
                if (block != null) Destroy(block);
            }
            topLayerBlocks.Clear();

            foreach (var block in bottomLayerBlocks.Values)
            {
                if (block != null) Destroy(block);
            }
            bottomLayerBlocks.Clear();
        }

        // Check if there's a solid block to stand on
        public bool HasSolidGround(CellCoord coord)
        {
            if (!IsValidCoord(coord))
                return false;

            // Can stand on dirt/coin blocks or bedrock if top is air
            BlockType top = grid[coord.x, coord.y].topLayer;
            if (top == BlockType.Dirt || top == BlockType.CoinBlock)
                return true;

            // If top is air, can stand on bedrock
            if (top == BlockType.Air)
                return true;

            return false;
        }

        // Get the height player should be at for a given coordinate
        public float GetStandingHeight(CellCoord coord)
        {
            if (!IsValidCoord(coord))
                return 0;

            BlockType top = grid[coord.x, coord.y].topLayer;
            if (top == BlockType.Dirt || top == BlockType.CoinBlock)
                return layerHeight + 0.5f; // Stand on top of dirt
            else
                return 0.5f; // Stand on bedrock
        }
    }
}

using UnityEngine;
using System.Collections.Generic;

namespace BrickDigger
{
    [System.Serializable]
    public class LevelConfig
    {
        public int levelNumber;
        public int width = 7;
        public int height = 15;
        public int coinsCount = 3;
        public int axesStart = 25;
        public List<CellCoord> pieceShape;
        public CellCoord piecePosition;

        // Tetromino shapes
        public static readonly Dictionary<string, List<CellCoord>> TetrominoShapes = new Dictionary<string, List<CellCoord>>
        {
            ["I"] = new List<CellCoord> { new CellCoord(0,0), new CellCoord(0,1), new CellCoord(0,2), new CellCoord(0,3) },
            ["O"] = new List<CellCoord> { new CellCoord(0,0), new CellCoord(0,1), new CellCoord(1,0), new CellCoord(1,1) },
            ["T"] = new List<CellCoord> { new CellCoord(1,0), new CellCoord(0,1), new CellCoord(1,1), new CellCoord(2,1) },
            ["L"] = new List<CellCoord> { new CellCoord(0,0), new CellCoord(0,1), new CellCoord(0,2), new CellCoord(1,2) },
            ["J"] = new List<CellCoord> { new CellCoord(1,0), new CellCoord(1,1), new CellCoord(1,2), new CellCoord(0,2) },
            ["S"] = new List<CellCoord> { new CellCoord(1,0), new CellCoord(2,0), new CellCoord(0,1), new CellCoord(1,1) },
            ["Z"] = new List<CellCoord> { new CellCoord(0,0), new CellCoord(1,0), new CellCoord(1,1), new CellCoord(2,1) }
        };

        public static LevelConfig GenerateLevel(int levelNumber)
        {
            LevelConfig config = new LevelConfig();
            config.levelNumber = levelNumber;

            // Increase difficulty with level
            int baseSize = 7;
            int sizeIncrease = (levelNumber - 1) / 3; // Increase every 3 levels
            config.width = baseSize + sizeIncrease;
            config.height = 15;

            // Axes and coins scale with map size
            int totalCells = config.width * config.height;
            config.axesStart = Mathf.Max(20, totalCells / 4); // About 25% of cells
            config.coinsCount = 3 + (levelNumber / 2); // More coins at higher levels

            // Select random tetromino
            string[] shapes = new string[] { "I", "O", "T", "L", "J", "S", "Z" };
            string selectedShape = shapes[Random.Range(0, shapes.Length)];
            config.pieceShape = new List<CellCoord>(TetrominoShapes[selectedShape]);

            // Random position for the piece (ensure it fits)
            int maxX = config.width - GetMaxX(config.pieceShape) - 1;
            int maxY = config.height - GetMaxY(config.pieceShape) - 1;
            config.piecePosition = new CellCoord(
                Random.Range(1, Mathf.Max(2, maxX)),
                Random.Range(1, Mathf.Max(2, maxY))
            );

            // Randomly rotate the piece
            int rotations = Random.Range(0, 4);
            for (int i = 0; i < rotations; i++)
            {
                config.pieceShape = RotatePiece(config.pieceShape);
            }

            return config;
        }

        private static List<CellCoord> RotatePiece(List<CellCoord> piece)
        {
            List<CellCoord> rotated = new List<CellCoord>();
            foreach (var coord in piece)
            {
                // 90 degree rotation: (x,y) -> (y,-x)
                rotated.Add(new CellCoord(coord.y, -coord.x));
            }

            // Normalize to positive coordinates
            int minX = int.MaxValue;
            int minY = int.MaxValue;
            foreach (var coord in rotated)
            {
                minX = Mathf.Min(minX, coord.x);
                minY = Mathf.Min(minY, coord.y);
            }

            for (int i = 0; i < rotated.Count; i++)
            {
                rotated[i] = new CellCoord(rotated[i].x - minX, rotated[i].y - minY);
            }

            return rotated;
        }

        private static int GetMaxX(List<CellCoord> piece)
        {
            int max = 0;
            foreach (var coord in piece)
                max = Mathf.Max(max, coord.x);
            return max;
        }

        private static int GetMaxY(List<CellCoord> piece)
        {
            int max = 0;
            foreach (var coord in piece)
                max = Mathf.Max(max, coord.y);
            return max;
        }
    }
}

using UnityEngine;
using UnityEngine.InputSystem;
using System.Collections;

namespace BrickDigger
{
    public class PlayerController : MonoBehaviour
    {
        [Header("Movement Settings")]
        [SerializeField] private float moveSpeed = 5f;
        [SerializeField] private float jumpForce = 5f;
        [SerializeField] private float gravity = -9.81f;
        [SerializeField] private float autoJumpHeight = 1.2f; // Height difference to trigger auto-jump

        [Header("Digging Settings")]
        [SerializeField] private float digDuration = 0.3f;
        [SerializeField] private float digCooldown = 0.5f;

        [Header("References")]
        [SerializeField] private GridManager gridManager;
        [SerializeField] private GameManager gameManager;

        private CharacterController characterController;
        private Animator animator;

        private Vector2 moveInput;
        private Vector3 velocity;
        private bool isGrounded;
        private bool isDigging;
        private bool canDig = true;
        private bool isOnBedrock = false;

        private CellCoord currentCell;
        private float targetHeight;

        // Mobile input actions
        private bool jumpRequested;
        private bool digRequested;

        private void Awake()
        {
            characterController = GetComponent<CharacterController>();
            if (characterController == null)
            {
                characterController = gameObject.AddComponent<CharacterController>();
                characterController.radius = 0.3f;
                characterController.height = 1f;
                characterController.center = new Vector3(0, 0.5f, 0);
            }

            animator = GetComponent<Animator>();
        }

        private void Start()
        {
            if (gridManager == null)
                gridManager = FindObjectOfType<GridManager>();
            if (gameManager == null)
                gameManager = FindObjectOfType<GameManager>();

            // Start at a valid position
            PlaceOnGrid(new CellCoord(gridManager.Width / 2, gridManager.Height / 2));
        }

        private void Update()
        {
            UpdateGroundCheck();
            UpdateCurrentCell();
            HandleMovement();
            HandleDigging();

            // Highlight block under player
            if (gridManager != null && !isDigging)
            {
                gridManager.HighlightBlock(currentCell);
            }
        }

        private void UpdateGroundCheck()
        {
            // Simple ground check
            isGrounded = characterController.isGrounded;

            // Check if we're on bedrock (no blocks above us)
            if (gridManager != null)
            {
                BlockType topBlock = gridManager.GetTopBlockAt(currentCell);
                isOnBedrock = (topBlock == BlockType.Air);
            }
        }

        private void UpdateCurrentCell()
        {
            if (gridManager != null)
            {
                currentCell = gridManager.WorldToGrid(transform.position);
            }
        }

        private void HandleMovement()
        {
            if (isDigging)
                return;

            Vector3 move = new Vector3(moveInput.x, 0, moveInput.y);

            if (move.magnitude > 0.1f)
            {
                // Check for auto-jump
                Vector3 moveDir = move.normalized;
                Vector3 nextPos = transform.position + moveDir * moveSpeed * Time.deltaTime;
                CellCoord nextCell = gridManager.WorldToGrid(nextPos);

                if (gridManager.IsValidCoord(nextCell))
                {
                    float currentHeight = gridManager.GetStandingHeight(currentCell);
                    float nextHeight = gridManager.GetStandingHeight(nextCell);

                    // Auto-jump if moving to higher ground
                    if (nextHeight > currentHeight && isGrounded)
                    {
                        velocity.y = Mathf.Sqrt(jumpForce * -2f * gravity);
                    }

                    // If on bedrock and trying to move to dirt, jump
                    if (isOnBedrock && gridManager.GetTopBlockAt(nextCell) != BlockType.Air)
                    {
                        velocity.y = Mathf.Sqrt(jumpForce * -2f * gravity);
                    }
                }

                // Apply horizontal movement
                characterController.Move(move * moveSpeed * Time.deltaTime);

                // Face movement direction
                if (move != Vector3.zero)
                {
                    transform.rotation = Quaternion.Slerp(transform.rotation, 
                        Quaternion.LookRotation(move), Time.deltaTime * 10f);
                }
            }

            // Handle manual jump
            if (jumpRequested && isGrounded)
            {
                velocity.y = Mathf.Sqrt(jumpForce * -2f * gravity);
                jumpRequested = false;
            }

            // Apply gravity
            if (isGrounded && velocity.y < 0)
            {
                velocity.y = -2f;
            }
            else
            {
                velocity.y += gravity * Time.deltaTime;
            }

            characterController.Move(velocity * Time.deltaTime);
        }

        private void HandleDigging()
        {
            if (digRequested && canDig && !isDigging && isGrounded)
            {
                StartCoroutine(DigBlock());
                digRequested = false;
            }
        }

        private IEnumerator DigBlock()
        {
            if (!gameManager.CanDig())
            {
                // Show feedback that no axes left
                Debug.Log("No axes left!");
                yield break;
            }

            isDigging = true;
            canDig = false;

            // Play dig animation
            if (animator != null)
            {
                animator.SetTrigger("Dig");
            }

            yield return new WaitForSeconds(digDuration);

            // Actually dig the block
            bool foundCoin, foundPiece;
            if (gridManager.DigBlock(currentCell, out foundCoin, out foundPiece))
            {
                gameManager.UseAxe();

                if (foundCoin)
                {
                    gameManager.CollectCoin();
                }

                if (foundPiece)
                {
                    gameManager.RevealPiece();
                }

                // Fall if we dug the block we're standing on
                float newHeight = gridManager.GetStandingHeight(currentCell);
                if (newHeight < transform.position.y - 0.5f)
                {
                    // We'll fall naturally with gravity
                }
            }

            isDigging = false;

            yield return new WaitForSeconds(digCooldown);
            canDig = true;
        }

        public void PlaceOnGrid(CellCoord coord)
        {
            if (gridManager != null && gridManager.IsValidCoord(coord))
            {
                float height = gridManager.GetStandingHeight(coord);
                transform.position = new Vector3(coord.x, height, coord.y);
                currentCell = coord;
            }
        }

        // Input System callbacks
        public void OnMove(Vector2 input)
        {
            moveInput = input;
        }

        public void OnJump()
        {
            jumpRequested = true;
        }

        public void OnDig()
        {
            digRequested = true;
        }

        // For mobile joystick
        public void SetMoveInput(Vector2 input)
        {
            moveInput = input;
        }

        public void SetJumpInput(bool jump)
        {
            jumpRequested = jump;
        }

        public void SetDigInput(bool dig)
        {
            digRequested = dig;
        }
    }
}

using UnityEngine;
using UnityEngine.Events;
using System.Collections;

namespace BrickDigger
{
    public class GameManager : MonoBehaviour
    {
        [Header("Game Settings")]
        [SerializeField] private int currentLevel = 1;
        [SerializeField] private int totalCoins = 0;

        [Header("Level Settings")]
        [SerializeField] private int axesRemaining = 25;
        [SerializeField] private int levelCoins = 0;
        [SerializeField] private int totalPieces = 0;
        [SerializeField] private int revealedPieces = 0;

        [Header("References")]
        [SerializeField] private GridManager gridManager;
        [SerializeField] private PlayerController playerController;
        [SerializeField] private UIManager uiManager;

        [Header("Events")]
        public UnityEvent OnLevelStart;
        public UnityEvent OnLevelWin;
        public UnityEvent OnLevelLose;
        public UnityEvent<int> OnAxesChanged;
        public UnityEvent<int> OnCoinsChanged;
        public UnityEvent<int, int> OnPieceRevealed; // current, total

        private LevelConfig currentLevelConfig;
        private bool isGameActive = false;

        private void Awake()
        {
            // Find references if not set
            if (gridManager == null)
                gridManager = FindObjectOfType<GridManager>();
            if (playerController == null)
                playerController = FindObjectOfType<PlayerController>();
            if (uiManager == null)
                uiManager = FindObjectOfType<UIManager>();
        }

        private void Start()
        {
            // Load saved progress
            LoadGameProgress();

            // Start first level
            StartLevel(currentLevel);
        }

        public void StartLevel(int levelNumber)
        {
            currentLevel = levelNumber;
            isGameActive = true;

            // Generate level config
            currentLevelConfig = LevelConfig.GenerateLevel(levelNumber);

            // Reset level state
            axesRemaining = currentLevelConfig.axesStart;
            levelCoins = 0;
            revealedPieces = 0;

            // Generate the level
            if (gridManager != null)
            {
                gridManager.GenerateLevel(currentLevelConfig);
                totalPieces = gridManager.CountLegoPieces();
            }

            // Place player at starting position
            if (playerController != null)
            {
                CellCoord startPos = new CellCoord(
                    currentLevelConfig.width / 2,
                    currentLevelConfig.height / 2
                );
                playerController.PlaceOnGrid(startPos);
            }

            // Update UI
            UpdateUI();

            // Fire event
            OnLevelStart?.Invoke();
        }

        public void RestartLevel()
        {
            StartLevel(currentLevel);
        }

        public void NextLevel()
        {
            currentLevel++;
            SaveGameProgress();
            StartLevel(currentLevel);
        }

        public bool CanDig()
        {
            return axesRemaining > 0 && isGameActive;
        }

        public void UseAxe()
        {
            if (axesRemaining > 0)
            {
                axesRemaining--;
                OnAxesChanged?.Invoke(axesRemaining);
                UpdateUI();

                // Check lose condition
                if (axesRemaining <= 0 && revealedPieces < totalPieces)
                {
                    StartCoroutine(LoseLevel());
                }
            }
        }

        public void CollectCoin()
        {
            levelCoins++;
            totalCoins++;
            OnCoinsChanged?.Invoke(levelCoins);
            UpdateUI();

            // Play coin collect effect
            if (uiManager != null)
            {
                uiManager.ShowCoinCollectEffect();
            }
        }

        public void RevealPiece()
        {
            revealedPieces++;
            OnPieceRevealed?.Invoke(revealedPieces, totalPieces);
            UpdateUI();

            // Check win condition
            if (revealedPieces >= totalPieces)
            {
                StartCoroutine(WinLevel());
            }
        }

        public void BuyAxes()
        {
            int axeCost = 5;
            int axePack = 3;

            if (totalCoins >= axeCost)
            {
                totalCoins -= axeCost;
                axesRemaining += axePack;
                OnAxesChanged?.Invoke(axesRemaining);
                OnCoinsChanged?.Invoke(totalCoins);
                UpdateUI();
            }
        }

        private IEnumerator WinLevel()
        {
            isGameActive = false;

            yield return new WaitForSeconds(0.5f);

            // Award bonus coins for winning
            int bonusCoins = 5;
            totalCoins += bonusCoins;

            // Show win UI
            if (uiManager != null)
            {
                uiManager.ShowWinPanel(levelCoins, bonusCoins, axesRemaining);
            }

            // Unlock next level
            int highestLevel = PlayerPrefs.GetInt("HighestLevel", 1);
            if (currentLevel >= highestLevel)
            {
                PlayerPrefs.SetInt("HighestLevel", currentLevel + 1);
            }

            SaveGameProgress();
            OnLevelWin?.Invoke();
        }

        private IEnumerator LoseLevel()
        {
            isGameActive = false;

            yield return new WaitForSeconds(0.5f);

            // Show lose UI
            if (uiManager != null)
            {
                uiManager.ShowLosePanel(totalCoins >= 5);
            }

            OnLevelLose?.Invoke();
        }

        private void UpdateUI()
        {
            if (uiManager != null)
            {
                uiManager.UpdateAxesDisplay(axesRemaining);
                uiManager.UpdateCoinsDisplay(levelCoins, totalCoins);
                uiManager.UpdatePieceProgress(revealedPieces, totalPieces);
            }
        }

        private void SaveGameProgress()
        {
            PlayerPrefs.SetInt("CurrentLevel", currentLevel);
            PlayerPrefs.SetInt("TotalCoins", totalCoins);
            PlayerPrefs.SetInt("HighestLevel", Mathf.Max(currentLevel, PlayerPrefs.GetInt("HighestLevel", 1)));
            PlayerPrefs.Save();
        }

        private void LoadGameProgress()
        {
            currentLevel = PlayerPrefs.GetInt("CurrentLevel", 1);
            totalCoins = PlayerPrefs.GetInt("TotalCoins", 0);
        }

        // Debug methods
        [ContextMenu("Add 10 Axes")]
        private void Debug_AddAxes()
        {
            axesRemaining += 10;
            UpdateUI();
        }

        [ContextMenu("Add 10 Coins")]
        private void Debug_AddCoins()
        {
            totalCoins += 10;
            UpdateUI();
        }

        [ContextMenu("Win Level")]
        private void Debug_WinLevel()
        {
            StartCoroutine(WinLevel());
        }
    }
}

using UnityEngine;
using UnityEngine.UI;
using TMPro;
using System.Collections;

namespace BrickDigger
{
    public class UIManager : MonoBehaviour
    {
        [Header("HUD Elements")]
        [SerializeField] private TextMeshProUGUI axesText;
        [SerializeField] private TextMeshProUGUI coinsText;
        [SerializeField] private TextMeshProUGUI pieceProgressText;
        [SerializeField] private Image pieceProgressBar;
        [SerializeField] private Button buyAxesButton;

        [Header("Win/Lose Panels")]
        [SerializeField] private GameObject winPanel;
        [SerializeField] private TextMeshProUGUI winCoinsText;
        [SerializeField] private TextMeshProUGUI winBonusText;
        [SerializeField] private TextMeshProUGUI winAxesText;
        [SerializeField] private Button nextLevelButton;
        [SerializeField] private Button retryButtonWin;

        [SerializeField] private GameObject losePanel;
        [SerializeField] private Button retryButton;
        [SerializeField] private Button buyAxesButtonLose;
        [SerializeField] private Button homeButton;

        [Header("Effects")]
        [SerializeField] private GameObject coinCollectPrefab;
        [SerializeField] private Transform effectsContainer;

        [Header("Mobile Controls")]
        [SerializeField] private MobileJoystick joystick;
        [SerializeField] private Button jumpButton;
        [SerializeField] private Button digButton;

        private GameManager gameManager;
        private PlayerController playerController;

        private void Awake()
        {
            gameManager = FindObjectOfType<GameManager>();
            playerController = FindObjectOfType<PlayerController>();

            // Setup button listeners
            if (buyAxesButton != null)
                buyAxesButton.onClick.AddListener(() => gameManager?.BuyAxes());

            if (nextLevelButton != null)
                nextLevelButton.onClick.AddListener(() => gameManager?.NextLevel());

            if (retryButton != null)
                retryButton.onClick.AddListener(() => gameManager?.RestartLevel());

            if (retryButtonWin != null)
                retryButtonWin.onClick.AddListener(() => gameManager?.RestartLevel());

            if (buyAxesButtonLose != null)
                buyAxesButtonLose.onClick.AddListener(() => {
                    gameManager?.BuyAxes();
                    HideLosePanel();
                });

            if (homeButton != null)
                homeButton.onClick.AddListener(() => LoadMainMenu());

            // Setup mobile controls
            if (jumpButton != null)
                jumpButton.onClick.AddListener(() => playerController?.OnJump());

            if (digButton != null)
                digButton.onClick.AddListener(() => playerController?.OnDig());

            // Hide panels initially
            if (winPanel != null) winPanel.SetActive(false);
            if (losePanel != null) losePanel.SetActive(false);
        }

        private void Update()
        {
            // Update joystick input
            if (joystick != null && playerController != null)
            {
                Vector2 input = joystick.GetInput();
                playerController.SetMoveInput(input);
            }
        }

        public void UpdateAxesDisplay(int axes)
        {
            if (axesText != null)
                axesText.text = $"Axes: {axes}";

            // Update buy button visibility
            if (buyAxesButton != null)
            {
                buyAxesButton.gameObject.SetActive(axes < 5);
            }
        }

        public void UpdateCoinsDisplay(int levelCoins, int totalCoins)
        {
            if (coinsText != null)
                coinsText.text = $"Coins: {levelCoins} ({totalCoins})";
        }

        public void UpdatePieceProgress(int revealed, int total)
        {
            if (pieceProgressText != null)
                pieceProgressText.text = $"Piece: {revealed}/{total}";

            if (pieceProgressBar != null && total > 0)
            {
                pieceProgressBar.fillAmount = (float)revealed / total;
            }
        }

        public void ShowWinPanel(int levelCoins, int bonusCoins, int axesLeft)
        {
            if (winPanel != null)
            {
                winPanel.SetActive(true);

                if (winCoinsText != null)
                    winCoinsText.text = $"Coins Collected: {levelCoins}";
                if (winBonusText != null)
                    winBonusText.text = $"Win Bonus: +{bonusCoins}";
                if (winAxesText != null)
                    winAxesText.text = $"Axes Saved: {axesLeft}";
            }
        }

        public void ShowLosePanel(bool canBuyAxes)
        {
            if (losePanel != null)
            {
                losePanel.SetActive(true);

                if (buyAxesButtonLose != null)
                    buyAxesButtonLose.gameObject.SetActive(canBuyAxes);
            }
        }

        public void HideLosePanel()
        {
            if (losePanel != null)
                losePanel.SetActive(false);
        }

        public void ShowCoinCollectEffect()
        {
            if (coinCollectPrefab != null && effectsContainer != null)
            {
                GameObject effect = Instantiate(coinCollectPrefab, effectsContainer);
                Destroy(effect, 2f);
            }
            else
            {
                // Simple text animation if no prefab
                StartCoroutine(CoinCollectAnimation());
            }
        }

        private IEnumerator CoinCollectAnimation()
        {
            if (coinsText != null)
            {
                Vector3 originalScale = coinsText.transform.localScale;
                coinsText.transform.localScale = originalScale * 1.5f;
                yield return new WaitForSeconds(0.2f);
                coinsText.transform.localScale = originalScale;
            }
        }

        private void LoadMainMenu()
        {
            // For now, just restart the level
            // In a full game, this would load the main menu scene
            gameManager?.StartLevel(1);
        }
    }
}

using UnityEngine;
using UnityEngine.EventSystems;
using UnityEngine.UI;

namespace BrickDigger
{
    public class MobileJoystick : MonoBehaviour, IPointerDownHandler, IPointerUpHandler, IDragHandler
    {
        [Header("Joystick Settings")]
        [SerializeField] private float joystickRadius = 100f;
        [SerializeField] private float deadZone = 0.1f;

        [Header("Visual Elements")]
        [SerializeField] private RectTransform joystickBackground;
        [SerializeField] private RectTransform joystickHandle;

        private Vector2 inputVector;
        private Vector2 joystickCenter;
        private bool isActive = false;

        private void Start()
        {
            if (joystickBackground == null)
                joystickBackground = GetComponent<RectTransform>();

            if (joystickHandle == null && transform.childCount > 0)
                joystickHandle = transform.GetChild(0).GetComponent<RectTransform>();

            // Get the center position
            joystickCenter = joystickBackground.position;
        }

        public void OnPointerDown(PointerEventData eventData)
        {
            isActive = true;
            OnDrag(eventData);
        }

        public void OnPointerUp(PointerEventData eventData)
        {
            isActive = false;
            inputVector = Vector2.zero;

            // Reset handle position
            if (joystickHandle != null)
            {
                joystickHandle.anchoredPosition = Vector2.zero;
            }
        }

        public void OnDrag(PointerEventData eventData)
        {
            if (!isActive) return;

            Vector2 position;
            RectTransformUtility.ScreenPointToLocalPointInRectangle(
                joystickBackground,
                eventData.position,
                eventData.pressEventCamera,
                out position
            );

            // Clamp to joystick radius
            position = Vector2.ClampMagnitude(position, joystickRadius);

            // Update handle position
            if (joystickHandle != null)
            {
                joystickHandle.anchoredPosition = position;
            }

            // Calculate input vector
            inputVector = position / joystickRadius;

            // Apply dead zone
            if (inputVector.magnitude < deadZone)
            {
                inputVector = Vector2.zero;
            }
            else
            {
                // Normalize considering dead zone
                inputVector = inputVector.normalized * ((inputVector.magnitude - deadZone) / (1 - deadZone));
            }
        }

        public Vector2 GetInput()
        {
            return inputVector;
        }

        public float GetHorizontal()
        {
            return inputVector.x;
        }

        public float GetVertical()
        {
            return inputVector.y;
        }

        public bool IsActive()
        {
            return isActive;
        }
    }
}

using UnityEngine;

namespace BrickDigger
{
    public class CameraFollow : MonoBehaviour
    {
        [Header("Target Settings")]
        [SerializeField] private Transform target;
        [SerializeField] private Vector3 offset = new Vector3(0, 10, -5);
        [SerializeField] private float lookAngle = 45f;

        [Header("Follow Settings")]
        [SerializeField] private float smoothSpeed = 5f;
        [SerializeField] private bool lockRotation = true;

        [Header("Bounds")]
        [SerializeField] private bool useBounds = false;
        [SerializeField] private Vector2 minBounds = new Vector2(-10, -10);
        [SerializeField] private Vector2 maxBounds = new Vector2(10, 10);

        private void Awake()
        {
            // Find player if not set
            if (target == null)
            {
                GameObject player = GameObject.FindGameObjectWithTag("Player");
                if (player != null)
                    target = player.transform;
            }
        }

        private void LateUpdate()
        {
            if (target == null)
                return;

            // Calculate desired position
            Vector3 desiredPosition = target.position + offset;

            // Apply bounds if needed
            if (useBounds)
            {
                desiredPosition.x = Mathf.Clamp(desiredPosition.x, minBounds.x, maxBounds.x);
                desiredPosition.z = Mathf.Clamp(desiredPosition.z, minBounds.y, maxBounds.y);
            }

            // Smooth movement
            Vector3 smoothedPosition = Vector3.Lerp(transform.position, desiredPosition, smoothSpeed * Time.deltaTime);
            transform.position = smoothedPosition;

            // Look at target
            if (lockRotation)
            {
                transform.rotation = Quaternion.Euler(lookAngle, 0, 0);
            }
            else
            {
                Vector3 lookDirection = target.position - transform.position;
                if (lookDirection != Vector3.zero)
                {
                    Quaternion targetRotation = Quaternion.LookRotation(lookDirection);
                    transform.rotation = Quaternion.Slerp(transform.rotation, targetRotation, smoothSpeed * Time.deltaTime);
                }
            }
        }

        public void SetTarget(Transform newTarget)
        {
            target = newTarget;
        }

        public void SetOffset(Vector3 newOffset)
        {
            offset = newOffset;
        }

        public void SetBounds(Vector2 min, Vector2 max)
        {
            minBounds = min;
            maxBounds = max;
            useBounds = true;
        }
    }
}

