using UnityEngine;
using UnityEditor;
using BrickDigger;
using UnityEngine.UI;
using TMPro;

public static class ConnectReferences
{
    [MenuItem("BrickDigger/Connect All References")]
    public static void ConnectAllReferences()
    {
        // Find all managers
        GridManager gridManager = Object.FindObjectOfType<GridManager>();
        GameManager gameManager = Object.FindObjectOfType<GameManager>();
        PlayerController playerController = Object.FindObjectOfType<PlayerController>();
        UIManager uiManager = Object.FindObjectOfType<UIManager>();
        CameraFollow cameraFollow = Object.FindObjectOfType<CameraFollow>();
        MobileJoystick joystick = Object.FindObjectOfType<MobileJoystick>();

        // Connect GameManager references
        if (gameManager != null)
        {
            var gmType = gameManager.GetType();
            gmType.GetField("gridManager", System.Reflection.BindingFlags.NonPublic | System.Reflection.BindingFlags.Instance)?.SetValue(gameManager, gridManager);
            gmType.GetField("playerController", System.Reflection.BindingFlags.NonPublic | System.Reflection.BindingFlags.Instance)?.SetValue(gameManager, playerController);
            gmType.GetField("uiManager", System.Reflection.BindingFlags.NonPublic | System.Reflection.BindingFlags.Instance)?.SetValue(gameManager, uiManager);
        }

        // Connect PlayerController references
        if (playerController != null)
        {
            var pcType = playerController.GetType();
            pcType.GetField("gridManager", System.Reflection.BindingFlags.NonPublic | System.Reflection.BindingFlags.Instance)?.SetValue(playerController, gridManager);
            pcType.GetField("gameManager", System.Reflection.BindingFlags.NonPublic | System.Reflection.BindingFlags.Instance)?.SetValue(playerController, gameManager);
        }

        // Connect UIManager references
        if (uiManager != null)
        {
            var uiType = uiManager.GetType();

            // Find UI elements
            TextMeshProUGUI axesText = GameObject.Find("AxesText")?.GetComponent<TextMeshProUGUI>();
            TextMeshProUGUI coinsText = GameObject.Find("CoinsText")?.GetComponent<TextMeshProUGUI>();
            TextMeshProUGUI pieceProgressText = GameObject.Find("PieceProgressText")?.GetComponent<TextMeshProUGUI>();
            Button jumpButton = GameObject.Find("JumpButton")?.GetComponent<Button>();
            Button digButton = GameObject.Find("DigButton")?.GetComponent<Button>();
            GameObject winPanel = GameObject.Find("WinPanel");
            GameObject losePanel = GameObject.Find("LosePanel");

            uiType.GetField("axesText", System.Reflection.BindingFlags.NonPublic | System.Reflection.BindingFlags.Instance)?.SetValue(uiManager, axesText);
            uiType.GetField("coinsText", System.Reflection.BindingFlags.NonPublic | System.Reflection.BindingFlags.Instance)?.SetValue(uiManager, coinsText);
            uiType.GetField("pieceProgressText", System.Reflection.BindingFlags.NonPublic | System.Reflection.BindingFlags.Instance)?.SetValue(uiManager, pieceProgressText);
            uiType.GetField("jumpButton", System.Reflection.BindingFlags.NonPublic | System.Reflection.BindingFlags.Instance)?.SetValue(uiManager, jumpButton);
            uiType.GetField("digButton", System.Reflection.BindingFlags.NonPublic | System.Reflection.BindingFlags.Instance)?.SetValue(uiManager, digButton);
            uiType.GetField("joystick", System.Reflection.BindingFlags.NonPublic | System.Reflection.BindingFlags.Instance)?.SetValue(uiManager, joystick);
            uiType.GetField("winPanel", System.Reflection.BindingFlags.NonPublic | System.Reflection.BindingFlags.Instance)?.SetValue(uiManager, winPanel);
            uiType.GetField("losePanel", System.Reflection.BindingFlags.NonPublic | System.Reflection.BindingFlags.Instance)?.SetValue(uiManager, losePanel);
        }

        // Connect GridManager prefab references
        if (gridManager != null)
        {
            GameObject dirtPrefab = AssetDatabase.LoadAssetAtPath<GameObject>("Assets/Prefabs/Blocks/DirtBlock.prefab");
            GameObject bedrockPrefab = AssetDatabase.LoadAssetAtPath<GameObject>("Assets/Prefabs/Blocks/BedrockBlock.prefab");
            GameObject coinPrefab = AssetDatabase.LoadAssetAtPath<GameObject>("Assets/Prefabs/Blocks/CoinBlock.prefab");
            GameObject legoPrefab = AssetDatabase.LoadAssetAtPath<GameObject>("Assets/Prefabs/Blocks/LegoPiece.prefab");

            var gridType = gridManager.GetType();
            gridType.GetField("dirtBlockPrefab", System.Reflection.BindingFlags.NonPublic | System.Reflection.BindingFlags.Instance)?.SetValue(gridManager, dirtPrefab);
            gridType.GetField("bedrockBlockPrefab", System.Reflection.BindingFlags.NonPublic | System.Reflection.BindingFlags.Instance)?.SetValue(gridManager, bedrockPrefab);
            gridType.GetField("coinBlockPrefab", System.Reflection.BindingFlags.NonPublic | System.Reflection.BindingFlags.Instance)?.SetValue(gridManager, coinPrefab);
            gridType.GetField("legoPiecePrefab", System.Reflection.BindingFlags.NonPublic | System.Reflection.BindingFlags.Instance)?.SetValue(gridManager, legoPrefab);
        }

        // Connect CameraFollow to Player
        if (cameraFollow != null && playerController != null)
        {
            var cfType = cameraFollow.GetType();
            cfType.GetField("target", System.Reflection.BindingFlags.NonPublic | System.Reflection.BindingFlags.Instance)?.SetValue(cameraFollow, playerController.transform);
        }

        Debug.Log("All references connected!");
        EditorUtility.SetDirty(gridManager);
        EditorUtility.SetDirty(gameManager);
        EditorUtility.SetDirty(playerController);
        EditorUtility.SetDirty(uiManager);
        EditorUtility.SetDirty(cameraFollow);
    }
}

using UnityEngine;
using UnityEditor;
using BrickDigger;
using UnityEngine.UI;
using TMPro;

public static class FixUI
{
    [MenuItem("BrickDigger/Fix UI Text")]
    public static void FixUIText()
    {
        // Find all TextMeshProUGUI components
        TextMeshProUGUI[] texts = Object.FindObjectsOfType<TextMeshProUGUI>();

        foreach (var text in texts)
        {
            // Ensure text is visible
            text.color = Color.white;
            text.fontSize = 24;
            text.fontStyle = FontStyles.Bold;
            text.alignment = TextAlignmentOptions.Center;

            // Set default text values
            if (text.gameObject.name == "AxesText")
                text.text = "Axes: 25";
            else if (text.gameObject.name == "CoinsText")
                text.text = "Coins: 0 (0)";
            else if (text.gameObject.name == "PieceProgressText")
                text.text = "Piece: 0/4";
            else if (text.gameObject.name == "Text" && text.transform.parent.name == "JumpButton")
                text.text = "JUMP";
            else if (text.gameObject.name == "Text" && text.transform.parent.name == "DigButton")
                text.text = "DIG";

            EditorUtility.SetDirty(text);
        }

        // Fix button images
        Button[] buttons = Object.FindObjectsOfType<Button>();
        foreach (var button in buttons)
        {
            Image image = button.GetComponent<Image>();
            if (image != null)
            {
                if (button.name == "JumpButton" || button.name == "DigButton")
                {
                    image.color = new Color(0.2f, 0.5f, 0.8f, 0.8f); // Blue buttons
                }
            }
            EditorUtility.SetDirty(button);
        }

        // Fix joystick visibility
        MobileJoystick joystick = Object.FindObjectOfType<MobileJoystick>();
        if (joystick != null)
        {
            Image joystickBg = joystick.GetComponent<Image>();
            if (joystickBg != null)
            {
                joystickBg.color = new Color(0.3f, 0.3f, 0.3f, 0.5f);
                EditorUtility.SetDirty(joystickBg);
            }

            Transform handle = joystick.transform.Find("Handle");
            if (handle != null)
            {
                Image handleImg = handle.GetComponent<Image>();
                if (handleImg != null)
                {
                    handleImg.color = new Color(0.2f, 0.2f, 0.2f, 0.8f);
                    EditorUtility.SetDirty(handleImg);
                }
            }
        }

        Debug.Log("UI Text Fixed!");
    }

    [MenuItem("BrickDigger/Setup UI Layout")]
    public static void SetupUILayout()
    {
        Canvas canvas = Object.FindObjectOfType<Canvas>();
        if (canvas == null) return;

        // Find HUD Panel
        Transform hudPanel = canvas.transform.Find("HUDPanel");
        if (hudPanel == null) return;

        // Position text elements at top
        Transform axesText = hudPanel.Find("AxesText");
        if (axesText != null)
        {
            RectTransform rect = axesText.GetComponent<RectTransform>();
            rect.anchorMin = new Vector2(0, 1);
            rect.anchorMax = new Vector2(0, 1);
            rect.pivot = new Vector2(0, 1);
            rect.anchoredPosition = new Vector2(20, -20);
            rect.sizeDelta = new Vector2(300, 40);
        }

        Transform coinsText = hudPanel.Find("CoinsText");
        if (coinsText != null)
        {
            RectTransform rect = coinsText.GetComponent<RectTransform>();
            rect.anchorMin = new Vector2(0, 1);
            rect.anchorMax = new Vector2(0, 1);
            rect.pivot = new Vector2(0, 1);
            rect.anchoredPosition = new Vector2(20, -60);
            rect.sizeDelta = new Vector2(300, 40);
        }

        Transform pieceText = hudPanel.Find("PieceProgressText");
        if (pieceText != null)
        {
            RectTransform rect = pieceText.GetComponent<RectTransform>();
            rect.anchorMin = new Vector2(0.5f, 1);
            rect.anchorMax = new Vector2(0.5f, 1);
            rect.pivot = new Vector2(0.5f, 1);
            rect.anchoredPosition = new Vector2(0, -20);
            rect.sizeDelta = new Vector2(300, 40);
        }

        // Position mobile controls at bottom
        Transform joystick = hudPanel.Find("Joystick");
        if (joystick != null)
        {
            RectTransform rect = joystick.GetComponent<RectTransform>();
            rect.anchorMin = new Vector2(0, 0);
            rect.anchorMax = new Vector2(0, 0);
            rect.pivot = new Vector2(0, 0);
            rect.anchoredPosition = new Vector2(150, 150);
            rect.sizeDelta = new Vector2(250, 250);
        }

        Transform jumpButton = hudPanel.Find("JumpButton");
        if (jumpButton != null)
        {
            RectTransform rect = jumpButton.GetComponent<RectTransform>();
            rect.anchorMin = new Vector2(1, 0);
            rect.anchorMax = new Vector2(1, 0);
            rect.pivot = new Vector2(1, 0);
            rect.anchoredPosition = new Vector2(-200, 150);
            rect.sizeDelta = new Vector2(150, 150);
        }

        Transform digButton = hudPanel.Find("DigButton");
        if (digButton != null)
        {
            RectTransform rect = digButton.GetComponent<RectTransform>();
            rect.anchorMin = new Vector2(1, 0);
            rect.anchorMax = new Vector2(1, 0);
            rect.pivot = new Vector2(1, 0);
            rect.anchoredPosition = new Vector2(-30, 150);
            rect.sizeDelta = new Vector2(150, 150);
        }

        Debug.Log("UI Layout Setup Complete!");
    }
}

using UnityEngine;
using UnityEditor;
using BrickDigger;
using UnityEngine.UI;
using TMPro;

public static class CreateProperUI
{
    [MenuItem("BrickDigger/Create Proper UI")]
    public static void CreateUI()
    {
        // Find or create canvas
        Canvas canvas = Object.FindObjectOfType<Canvas>();
        if (canvas == null)
        {
            GameObject canvasObj = new GameObject("UICanvas");
            canvas = canvasObj.AddComponent<Canvas>();
            canvas.renderMode = RenderMode.ScreenSpaceOverlay;
            canvasObj.AddComponent<CanvasScaler>();
            canvasObj.AddComponent<GraphicRaycaster>();
        }

        // Clear existing HUD if any
        Transform oldHud = canvas.transform.Find("HUDPanel");
        if (oldHud != null)
            Object.DestroyImmediate(oldHud.gameObject);

        // Create HUD Panel
        GameObject hudPanel = CreatePanel("HUDPanel", canvas.transform);

        // Create simple text displays using Text component (not TMP)
        CreateSimpleText("AxesText", hudPanel.transform, "Axes: 25", 
            new Vector2(0, 1), new Vector2(20, -20));
        CreateSimpleText("CoinsText", hudPanel.transform, "Coins: 0 (0)", 
            new Vector2(0, 1), new Vector2(20, -60));
        CreateSimpleText("PieceProgressText", hudPanel.transform, "Piece: 0/4", 
            new Vector2(0.5f, 1), new Vector2(0, -20));

        // Create Mobile Controls
        CreateJoystick(hudPanel.transform);
        CreateControlButton("JumpButton", hudPanel.transform, "JUMP", 
            new Vector2(-200, 150), Color.blue);
        CreateControlButton("DigButton", hudPanel.transform, "DIG", 
            new Vector2(-30, 150), new Color(0.8f, 0.2f, 0.2f));

        // Create Win Panel
        CreateWinLosePanel("WinPanel", canvas.transform, "LEVEL COMPLETE!", true);
        CreateWinLosePanel("LosePanel", canvas.transform, "OUT OF AXES!", false);

        Debug.Log("Proper UI Created!");
    }

    private static GameObject CreatePanel(string name, Transform parent)
    {
        GameObject panel = new GameObject(name);
        panel.transform.SetParent(parent, false);
        RectTransform rect = panel.AddComponent<RectTransform>();
        rect.anchorMin = Vector2.zero;
        rect.anchorMax = Vector2.one;
        rect.offsetMin = Vector2.zero;
        rect.offsetMax = Vector2.zero;
        return panel;
    }

    private static GameObject CreateSimpleText(string name, Transform parent, string text, 
        Vector2 anchor, Vector2 position)
    {
        GameObject textObj = new GameObject(name);
        textObj.transform.SetParent(parent, false);

        RectTransform rect = textObj.AddComponent<RectTransform>();
        rect.anchorMin = anchor;
        rect.anchorMax = anchor;
        rect.pivot = new Vector2(0, 1);
        rect.anchoredPosition = position;
        rect.sizeDelta = new Vector2(300, 40);

        // Use regular Text component instead of TMP
        Text textComp = textObj.AddComponent<Text>();
        textComp.text = text;
        textComp.font = Font.CreateDynamicFontFromOSFont("Arial", 24);
        textComp.fontSize = 24;
        textComp.color = Color.white;
        textComp.alignment = TextAnchor.MiddleLeft;

        // Add outline for better visibility
        Outline outline = textObj.AddComponent<Outline>();
        outline.effectColor = Color.black;
        outline.effectDistance = new Vector2(2, -2);

        return textObj;
    }

    private static GameObject CreateJoystick(Transform parent)
    {
        GameObject joystickObj = new GameObject("Joystick");
        joystickObj.transform.SetParent(parent, false);

        RectTransform rect = joystickObj.AddComponent<RectTransform>();
        rect.anchorMin = new Vector2(0, 0);
        rect.anchorMax = new Vector2(0, 0);
        rect.pivot = new Vector2(0, 0);
        rect.anchoredPosition = new Vector2(150, 150);
        rect.sizeDelta = new Vector2(250, 250);

        Image bgImage = joystickObj.AddComponent<Image>();
        bgImage.color = new Color(0.3f, 0.3f, 0.3f, 0.5f);

        // Create handle
        GameObject handle = new GameObject("Handle");
        handle.transform.SetParent(joystickObj.transform, false);
        RectTransform handleRect = handle.AddComponent<RectTransform>();
