        [Header("Level Settings")]
        [SerializeField] private int axesRemaining = 25;
        [SerializeField] private int levelCoins = 0;
        [SerializeField] private int totalPieces = 0;
        [SerializeField] private int revealedPieces = 0;
        
        [Header("References")]
        [SerializeField] private GridManager gridManager;
        [SerializeField] private PlayerController playerController;
        [SerializeField] private UIManager uiManager;
        
        [Header("Events")]
        public UnityEvent OnLevelStart;
        public UnityEvent OnLevelWin;
        public UnityEvent OnLevelLose;
        public UnityEvent<int> OnAxesChanged;
        public UnityEvent<int> OnCoinsChanged;
        public UnityEvent<int, int> OnPieceRevealed; // current, total
        
        private LevelConfig currentLevelConfig;
        private bool isGameActive = false;
        
        private void Awake()
        {
            // Find references if not set
            if (gridManager == null)
                gridManager = FindObjectOfType<GridManager>();
            if (playerController == null)
                playerController = FindObjectOfType<PlayerController>();
            if (uiManager == null)
                uiManager = FindObjectOfType<UIManager>();
        }
        
        private void Start()
        {
            // Load saved progress
            LoadGameProgress();
            
            // Start first level
            StartLevel(currentLevel);
        }
        
        public void StartLevel(int levelNumber)
        {
            currentLevel = levelNumber;
            isGameActive = true;
            
            // Generate level config
            currentLevelConfig = LevelConfig.GenerateLevel(levelNumber);
            
            // Reset level state
            axesRemaining = currentLevelConfig.axesStart;
            levelCoins = 0;
            revealedPieces = 0;
            
            // Generate the level
            if (gridManager != null)
            {
                gridManager.GenerateLevel(currentLevelConfig);
                totalPieces = gridManager.CountLegoPieces();
            }
            
            // Place player at starting position
            if (playerController != null)
            {
                CellCoord startPos = new CellCoord(
                    currentLevelConfig.width / 2,
                    currentLevelConfig.height / 2
                );
                playerController.PlaceOnGrid(startPos);
            }
            
            // Update UI
            UpdateUI();
            
            // Fire event
            OnLevelStart?.Invoke();
        }
        
        public void RestartLevel()
        {
            StartLevel(currentLevel);
        }
        
        public void NextLevel()
        {
            currentLevel++;
            SaveGameProgress();
            StartLevel(currentLevel);
        }
        
        public bool CanDig()
        {
            return axesRemaining > 0 && isGameActive;
        }
        
        public void UseAxe()
        {
            if (axesRemaining > 0)
            {
                axesRemaining--;
                OnAxesChanged?.Invoke(axesRemaining);
                UpdateUI();
                
                // Check lose condition
                if (axesRemaining <= 0 && revealedPieces < totalPieces)
                {
                    StartCoroutine(LoseLevel());
                }
            }
        }
        
        public void CollectCoin()
        {
            levelCoins++;
            totalCoins++;
            OnCoinsChanged?.Invoke(levelCoins);
            UpdateUI();
            
            // Play coin collect effect
            if (uiManager != null)
            {
                uiManager.ShowCoinCollectEffect();
            }
        }
        
        public void RevealPiece()
        {
            revealedPieces++;
            OnPieceRevealed?.Invoke(revealedPieces, totalPieces);
            UpdateUI();
            
            // Check win condition
            if (revealedPieces >= totalPieces)
            {
                StartCoroutine(WinLevel());
            }
        }
        
        public void BuyAxes()
        {
            int axeCost = 5;
            int axePack = 3;
            
            if (totalCoins >= axeCost)
            {
                totalCoins -= axeCost;
                axesRemaining += axePack;
                OnAxesChanged?.Invoke(axesRemaining);
                OnCoinsChanged?.Invoke(totalCoins);
                UpdateUI();
            }
        }
        
        private IEnumerator WinLevel()
        {
            isGameActive = false;
            
            yield return new WaitForSeconds(0.5f);
            
            // Award bonus coins for winning
            int bonusCoins = 5;
            totalCoins += bonusCoins;
            
            // Show win UI
            if (uiManager != null)
            {
                uiManager.ShowWinPanel(levelCoins, bonusCoins, axesRemaining);
            }
            
            // Unlock next level
            int highestLevel = PlayerPrefs.GetInt("HighestLevel", 1);
            if (currentLevel >= highestLevel)
            {
                PlayerPrefs.SetInt("HighestLevel", currentLevel + 1);
            }
            
            SaveGameProgress();
            OnLevelWin?.Invoke();
        }
        
        private IEnumerator LoseLevel()
        {
            isGameActive = false;
            
            yield return new WaitForSeconds(0.5f);
            
            // Show lose UI
            if (uiManager != null)
            {
                uiManager.ShowLosePanel(totalCoins >= 5);
            }
            
            OnLevelLose?.Invoke();
        }
        
        private void UpdateUI()
        {
            if (uiManager != null)
            {
                uiManager.UpdateAxesDisplay(axesRemaining);
                uiManager.UpdateCoinsDisplay(levelCoins, totalCoins);
                uiManager.UpdatePieceProgress(revealedPieces, totalPieces);
            }
        }
        
        private void SaveGameProgress()
        {
            PlayerPrefs.SetInt("CurrentLevel", currentLevel);
            PlayerPrefs.SetInt("TotalCoins", totalCoins);
            PlayerPrefs.SetInt("HighestLevel", Mathf.Max(currentLevel, PlayerPrefs.GetInt("HighestLevel", 1)));
            PlayerPrefs.Save();
        }
        
        private void LoadGameProgress()
        {
            currentLevel = PlayerPrefs.GetInt("CurrentLevel", 1);
            totalCoins = PlayerPrefs.GetInt("TotalCoins", 0);
        }
        
        // Debug methods
        [ContextMenu("Add 10 Axes")]
        private void Debug_AddAxes()
        {
            axesRemaining += 10;
            UpdateUI();
        }
        
        [ContextMenu("Add 10 Coins")]
        private void Debug_AddCoins()
        {
            totalCoins += 10;
            UpdateUI();
        }
        
        [ContextMenu("Win Level")]
        private void Debug_WinLevel()
        {
            StartCoroutine(WinLevel());
        }
    }
}

using UnityEngine;
using System.Collections.Generic;

namespace BrickDigger
{
    [System.Serializable]
    public class LevelConfig
    {
        public int levelNumber;
        public int width = 7;
        public int height = 15;
        public int coinsCount = 3;
        public int axesStart = 25;
        public List<CellCoord> pieceShape;
        public CellCoord piecePosition;
        
        // Tetromino shapes
        public static readonly Dictionary<string, List<CellCoord>> TetrominoShapes = new Dictionary<string, List<CellCoord>>
        {
            ["I"] = new List<CellCoord> { new CellCoord(0,0), new CellCoord(0,1), new CellCoord(0,2), new CellCoord(0,3) },
            ["O"] = new List<CellCoord> { new CellCoord(0,0), new CellCoord(0,1), new CellCoord(1,0), new CellCoord(1,1) },
            ["T"] = new List<CellCoord> { new CellCoord(1,0), new CellCoord(0,1), new CellCoord(1,1), new CellCoord(2,1) },
            ["L"] = new List<CellCoord> { new CellCoord(0,0), new CellCoord(0,1), new CellCoord(0,2), new CellCoord(1,2) },
            ["J"] = new List<CellCoord> { new CellCoord(1,0), new CellCoord(1,1), new CellCoord(1,2), new CellCoord(0,2) },
            ["S"] = new List<CellCoord> { new CellCoord(1,0), new CellCoord(2,0), new CellCoord(0,1), new CellCoord(1,1) },
            ["Z"] = new List<CellCoord> { new CellCoord(0,0), new CellCoord(1,0), new CellCoord(1,1), new CellCoord(2,1) }
        };
        
        public static LevelConfig GenerateLevel(int levelNumber)
        {
            LevelConfig config = new LevelConfig();
            config.levelNumber = levelNumber;
            
            // Increase difficulty with level
            int baseSize = 7;
            int sizeIncrease = (levelNumber - 1) / 3; // Increase every 3 levels
            config.width = baseSize + sizeIncrease;
            config.height = 15;
            
            // Axes and coins scale with map size
            int totalCells = config.width * config.height;
            config.axesStart = Mathf.Max(20, totalCells / 4); // About 25% of cells
            config.coinsCount = 3 + (levelNumber / 2); // More coins at higher levels
            
            // Select random tetromino
            string[] shapes = new string[] { "I", "O", "T", "L", "J", "S", "Z" };
            string selectedShape = shapes[Random.Range(0, shapes.Length)];
            config.pieceShape = new List<CellCoord>(TetrominoShapes[selectedShape]);
            
            // Random position for the piece (ensure it fits)
            int maxX = config.width - GetMaxX(config.pieceShape) - 1;
            int maxY = config.height - GetMaxY(config.pieceShape) - 1;
            config.piecePosition = new CellCoord(
                Random.Range(1, Mathf.Max(2, maxX)),
                Random.Range(1, Mathf.Max(2, maxY))
            );
            
            // Randomly rotate the piece
            int rotations = Random.Range(0, 4);
            for (int i = 0; i < rotations; i++)
            {
                config.pieceShape = RotatePiece(config.pieceShape);
            }
            
            return config;
        }
        
        private static List<CellCoord> RotatePiece(List<CellCoord> piece)
        {
            List<CellCoord> rotated = new List<CellCoord>();
            foreach (var coord in piece)
            {
                // 90 degree rotation: (x,y) -> (y,-x)
                rotated.Add(new CellCoord(coord.y, -coord.x));
            }
            
            // Normalize to positive coordinates
            int minX = int.MaxValue;
            int minY = int.MaxValue;
            foreach (var coord in rotated)
            {
                minX = Mathf.Min(minX, coord.x);
                minY = Mathf.Min(minY, coord.y);
            }
            
            for (int i = 0; i < rotated.Count; i++)
            {
                rotated[i] = new CellCoord(rotated[i].x - minX, rotated[i].y - minY);
            }
            
            return rotated;
        }
        
        private static int GetMaxX(List<CellCoord> piece)
        {
            int max = 0;
            foreach (var coord in piece)
                max = Mathf.Max(max, coord.x);
            return max;
        }
        
        private static int GetMaxY(List<CellCoord> piece)
        {
            int max = 0;
            foreach (var coord in piece)
                max = Mathf.Max(max, coord.y);
            return max;
        }
    }
}

